function strf(str, toRaw) {
	if(typeof(toRaw) == typeof(undefined)) { var toRaw = false; }
	return strrawformat(str, toRaw);
}

function strrawformat(str, toRaw) {
	if(typeof(toRaw) == typeof(undefined)) { var toRaw = false; }
	var rf = [];
	var txt = '';
	var ri = -1;
	var isCode = false;
	var txtColor = 'white';
	var isItalic = 0;
	var isBold = 0;
	var isStrike = 0;
	var isUnderlined = 0;
	var isObf = 0;
	str = str+'&r ';
	
	for(var i = 0; i < str.length; i++) {
		var c = str.substr(i, 1);
		if(c == '&' || i == str.length-1) {
			//Check if new section has to be made
			if(txt.length > 0) {
				ri++;
				var cmds = [];
				
				rf.push([txt, txtColor, isItalic, isBold, isUnderlined, isStrike, isObf]);
				isItalic = false;
				isBold = false;
				isUnderlined = false;
				isStrike = false;
				isObf = false;
				txtColor = 'white';
				txt = '';
			}
			isCode = true;
			continue;
		} else {
			if(!isCode) {
				txt += c.toString();
			} else {
				//Check Colors
				if(typeof(_RAWCOLORS[c]) != typeof(undefined)) {
					txtColor = _RAWCOLORS[c];
				}
				//Check Markup
				switch(c.toString()) {
					case 'o': {
						isItalic = true;
						break;
					}
					case 'l': {
						isBold = true;
						break;
					}
					case 'n': {
						isUnderlined = true;
						break;
					}
					case 'm': {
						isStrike = true;
						break;
					}
					case 'k': {
						isObf = true;
						break;
					}
					case 'r': {
						isItalic = false;
						isBold = false;
						isUnderlined = false;
						isStrike = false;
						isObf = false;
						txtColor = 'white';
						break;
					}
				}
				isCode = false;
			}
		}
	}
	
	return (!toRaw ? rf : rawformat(rf));
}


function rawformat(str_pieces, fullraw) {
	if(typeof(fullraw) == typeof(undefined)) { var fullraw = true; }
	//print("FULLRAR: "+fullraw.toString());
	var txt = '';
	if(fullraw) { txt+='[""'; }
	
	for(i in str_pieces) {
		var p = str_pieces[i];
		p[0] = p[0].replace(/\"/g, '\\"');
		var pc = '{"text":"'+p[0].toString()+'"';
		if(p[1]) { pc+=',"color":"'+p[1].toString()+'"'; }
		if(p[2]) { pc+=',"italic":true'; }
		if(p[3]) { pc+=',"bold":true'; }
		if(p[4]) { pc+=',"underlined":true'; }
		if(p[5]) { pc+=',"strikethrough":true'; }
		if(p[6]) { pc+=',"obfuscated":true'; }
		if(p[7]) { pc+=',"clickEvent":{"action":"run_command","value":"'+p[6]+'"}'; }
		pc += '}';
		
		txt+=','+pc.toString();
	}
	
	if(fullraw) {
		txt += ']';
	}
	return txt;
	

}

